import Container from "../components/Container";
import React, { useState } from 'react';
import {getUrlEstadoCuenta, getUrlPago} from "../libraries/env"
import Estadocuenta from "../components/Estadocuenta"

const Index = () => {
     const [inpCed, inpCedValue] = useState('');
     const [loadinConsulta, setloadinConsulta] = useState('');
     const [inpIdFact, setinpIdFact] = useState('');
     const [inpPago, setinpPago] = useState('');
     const [estadoCta, setEstadoCta] = useState(null);

     async function getEstadoCuenta(e){
        e.preventDefault();
        if(inpCed.length > 0)
        {
            setEstadoCta(null);
            let proc = {
              method: 'POST',
              body: JSON.stringify({
                cliente : inpCed
              }), 
              headers:{
                'Content-Type': 'application/json',
              }
            };

            fetch(getUrlEstadoCuenta(), proc)
            .then(setloadinConsulta('Bucando....'))
            .then(result => { 
              return result.json()
            })
            .then(data => {
                console.log(data)
                setloadinConsulta('')
                //setEstadoCta(data);
            })
            .catch(error => {
              console.log(error);
              setloadinConsulta('errores...');
             })
        }
      }






 return(

  <Container>
    <div>
      <div className="flex flex-wrap">


        <div class="flex-1 text-center h-screen ">
          <h1 className="pt-10 text-3xl font-semibold">Estado de cuenta</h1>
          <div className="container flex flex-wrap my-10 px-6 justify-center">
            <div class="w-full md:w-1/2 mb-6 md:mb-0 text-left pr-6">
              <label
                class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-1 ml-1" for="imp_ced">
                cedula
              </label>
              <input
                id="imp_ced"
                class="block w-full text-gray-700 border rounded py-3 px-4 leading-tight focus:outline-none"
                id="grid-first-name"
                type="text"
                placeholder="0926327917"
                value={inpCed} onChange={(e) => inpCedValue(e.target.value)}
                />
            </div>

            <div className="flex content-end flex-wrap ">
            <button
              class="bg-indigo-600 hover:bg-indigo-700 border-indigo-600 hover:border-indigo-700 text-sm border-4 text-white py-2 px-2 rounded"
              type="button"
              onClick={e => getEstadoCuenta(e)}
            >
              Consultar
            </button>
            </div>
          </div>

          <p>{loadinConsulta}</p>
         {estadoCta && <Estadocuenta props={estadoCta}></Estadocuenta>}


            {/* <div className="container flex flex-col flex-wrap my-10 px-6 justify-center">
                <div className="border-b p-2 my-1 rounded text-left">
                    <p>
                        Id Factura: $20
                    </p>
                    <p>
                    Valor a pagar: $20
                    </p>
                </div>
                <div className="border-b p-2 my-1 rounded text-left">fdsdfsfsdf</div>
                <div className="border-b p-2 my-1 rounded text-left">fdsdfsfsdf</div>
            </div> */}


        </div>







        <div class="flex-1 bg-indigo-600 text-center bg-gray-400 h-screen">
          <h1 className="pt-10 text-3xl font-semibold text-white">Realizar Pagos</h1>
          <div className=" flex flex-wrap my-10 px-6 justify-center">
              <div className="flex-1 text-left px-2">
                <label
                    class="block uppercase tracking-wide text-gray-100 text-xs font-bold mb-1 ml-1" for="imp_idfact">
                    Id Factura
                </label>
                <input
                id="imp_idfact"
                class="block w-full text-gray-700 border rounded py-3 px-4 leading-tight focus:outline-none"
                id="grid-first-name"
                type="number"
                value={inpIdFact} onChange={(e) => setinpIdFact(e.target.value)}
                />
              </div>
              <div className="flex-1 text-left px-2">
                <label
                    class="block uppercase tracking-wide text-gray-100 text-xs font-bold mb-1 ml-1" for="imp_idfact">
                    Valor a pagar
                </label>
                <input
                id="imp_val"
                class="block w-full text-gray-700 border rounded py-3 px-4 leading-tight focus:outline-none"
                id="grid-first-name"
                type="text"
                value={inpPago} onChange={(e) => setinpPago(e.target.value)}
                />
              </div>
              <div className="flex content-end flex-wrap  text-left px-2">
                <button
                class="bg-indigo-300 hover:bg-indigo-800 border-indigo-300 hover:border-indigo-800 hover:text-white text-sm border-4 text-indigo-900 font-medium  py-2 px-6 rounded" type="button"

                >
                Pagar
                </button>
              </div>
          </div>

        </div>
      </div>
    </div>
  </Container>

    );




 }

export default Index;
